// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
* @author @0xJonaseb11
 * @title ChainPilot - Automation Agent
 * @notice Orchestrates tasks, handles gas, and integrates offchain agents/AI.
 * @dev Acts as a middleware between users and core contracts (Scheduler/Executer).
 */


////////////////////////////////////////////////////
/////  I THINK THIS CONTRACT IS NOT MUCH USEFUL ///
/////  SINCE Executor and Scheduler are already ////
///////////////////////////////////////////////////

contract AutomationAgent {
    // ------------------------ Custom Errors ------------------------
    error Unauthorized();
    error InsufficientGasCredit();
    error TaskConditionsNotMet();

    // ------------------------ Structs & Events ------------------------
    struct TaskConfig {
        address user;
        address target;
        bytes payload;
        uint256 gasCredit;  
        bytes32 conditionsHash;  
    }

    event TaskSubscribed(uint256 indexed taskId, address indexed user);
    event TaskAutoExecuted(uint256 indexed taskId, bool success);

    // ------------------------ Immutables ------------------------
    address public immutable scheduler;
    address public immutable executer;

    // ------------------------ Storage ------------------------
    mapping(uint256 => TaskConfig) public tasks;
    uint256 public taskIdCounter;

    // ------------------------ Constructor ------------------------
    constructor(address _scheduler, address _executer) {
        scheduler = _scheduler;
        executer = _executer;
    }

    // ------------------------ Core Functions ------------------------
    /**
     * @notice Subscribe to a new automated task (called by users).
     * @param target Contract to interact with (e.g., Executer.sol).
     * @param payload Encoded function call.
     * @param gasCredit ETH reserved for gas (use 0 for self-funded).
     * @param conditionsHash Offchain-checked conditions (e.g., "APY > 5%").
     */
    function subscribeTask(
        address target,
        bytes calldata payload,
        uint256 gasCredit,
        bytes32 conditionsHash
    ) external payable returns (uint256 taskId) {
        taskId = taskIdCounter++;
        tasks[taskId] = TaskConfig(
            msg.sender,
            target,
            payload,
            gasCredit,
            conditionsHash
        );

        if (gasCredit > 0) {
            // User prepays gas (optional)
            if (msg.value < gasCredit) revert InsufficientGasCredit();
        }

        emit TaskSubscribed(taskId, msg.sender);
    }

    /**
     * @notice Execute a task if conditions are met (called by offchain AI/keeper).
     * @dev Offchain agents validate conditionsHash before calling.
     */
    function executeTask(
        uint256 taskId,
        bytes calldata conditionsProof  // E.g., signed APY data
    ) external {
        TaskConfig memory config = tasks[taskId];
        if (config.user == address(0)) revert Unauthorized();

        if (!_validateConditions(config.conditionsHash, conditionsProof)) {
            revert TaskConditionsNotMet();
        }

        // Use prepaid gas or user's wallet
        uint256 gasToUse = config.gasCredit > 0 ? config.gasCredit : 0;

        (bool success, ) = executer.call{value: gasToUse}(
            abi.encodeWithSignature(
                "executeTask(address,address,bytes,uint256)",
                config.user,
                config.target,
                config.payload,
                0  // value
            )
        );

        emit TaskAutoExecuted(taskId, success);
    }

    // ------------------------ Internal ------------------------
    function _validateConditions(
        bytes32 conditionsHash,
        bytes calldata proof
    ) internal pure returns (bool) {
        // like.. Verify signed offchain data (forex.., APY > 5%)
        /////////////////////////////////////////////////////////////////////////////////
        //// ask Sipho if which oracle we'll have to use btn chainlink n gelato | flare//
        /////////////////////////////////////////////////////////////////////////////////
        return conditionsHash == keccak256(proof);
    }
}

contracts/core/Executor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @author @0xJonaseb11
 * @title ChainPilotExecutor Contract
 * @notice Securely executes pre-approved tasks from Scheduler
 * @dev Uses EIP-712 signatures for approvals and optimized for Base network
 */

///////////////////////////////////////////////////
////////   REMEMBER TO ASK SIPHO IF WE NEED AA ////
////////.  SO I CAN SETUP EntryPoint.sol ////////
////////////////////////////////////////////////

contract ChainPilotExecutor {

    // ------------------------ Custom Errors ------------------------
    error UnauthorizedExecuter(address user, bytes32 taskHash);
    error ExecutionFailed(bytes reason);
    error InvalidValue(uint256 expected, uint256 actual);
    error ExpiredApproval(uint256 deadline);

    // ------------------------ Events ------------------------
    event TaskApproved(
        address indexed user,
        bytes32 indexed taskHash,
        uint256 maxValue,
        uint256 deadline
    );
    event TaskExecuted(
        address indexed user,
        address indexed target,
        bytes32 indexed taskHash,
        uint256 value,
        bool success
    );

    // ------------------------ Storage ------------------------
    struct Approval {
        uint256 maxValue;
        uint256 deadline;
    }

    mapping(address => mapping(bytes32 => Approval)) public userApprovals;

    // ------------------------ External Functions ------------------------
    /**
     * @notice Pre-approve a task with deadline
     */
    function approveTask(
        address target,
        bytes calldata payload,
        uint256 maxValue,
        uint256 deadline
    ) external {
        bytes32 taskHash = getTaskHash(target, payload, maxValue);
        userApprovals[msg.sender][taskHash] = Approval(maxValue, deadline);
        emit TaskApproved(msg.sender, taskHash, maxValue, deadline);
    }

    /**
     * @notice Execute a pre-approved task from Scheduler
     */
    function executeTask(
        address user,
        address target,
        bytes32 payloadHash,
        uint256 value
    ) external payable returns (bool) {
        Approval memory approval = userApprovals[user][payloadHash];

        if (approval.deadline == 0) revert UnauthorizedExecuter(user, payloadHash);
        if (block.timestamp > approval.deadline) revert ExpiredApproval(approval.deadline);
        if (value > approval.maxValue) revert InvalidValue(approval.maxValue, value);
        if (msg.value != value) revert InvalidValue(value, msg.value);

        (bool success, bytes memory reason) = target.call{value: value}(
            abi.encodePacked(payloadHash, user)
        );

        if (!success) revert ExecutionFailed(reason);

        emit TaskExecuted(user, target, payloadHash, value, success);
        return success;
    }

    // ------------------------ Public Functions ------------------------
    /**
     * @notice Compute task hash for approval
     */
    function getTaskHash(
        address target,
        bytes calldata payload,
        uint256 maxValue
    ) public pure returns (bytes32) {
        return keccak256(abi.encode(target, keccak256(payload), maxValue));
    }
}

contracts/core/Scheduler.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/** 
 * @author @0xJonaseb11
 * @title ChainPilotScheduler Contract
 * @notice Queues time-based tasks for off-chain keepers to execute via Executer.
 * @dev Features tight integration with Executer contract, packed storage, and enhanced security.
 */

import { ChainPilotExecutor } from "./Executor.sol";

contract ChainPilotScheduler is ChainPilotExecutor {

    // ------------------------ Custom Errors ------------------------
    error Unauthorized(address caller, address owner);
    error InvalidTask(string reason);
    error TaskNotFound(uint256 taskId);
    error TaskAlreadyCancelled(uint256 taskId);
    error InvalidTimeWindow(uint256 from, uint256 to);
    error ExecutionWindowNotReached(uint256 executeAt, uint256 currentTime);
    error ExecutionWindowExpired(uint256 executeAt, uint256 expiry);

    // ------------------------ Events ------------------------
    event TaskScheduled(
        uint256 indexed taskId,
        address indexed user,
        address indexed executer,
        address target,
        uint64 executeAt,
        uint64 expiryAt,
        bytes32 payloadHash,
        uint256 value
    );
    event TaskCancelled(uint256 indexed taskId);
    event TaskExecuted(uint256 indexed taskId, address indexed executor);

    // ------------------------ Structs ------------------------
    struct Task {
        uint64 executeAt;       
        uint64 expiryAt;        
        address user;          
        address executer;      
        address target;         
        bytes32 payloadHash;    
        uint256 value;         
        bool isCancelled;      
    }

    // ------------------------ Storage ------------------------
    mapping(uint256 => Task) public tasks;
    uint256 public taskIdCounter;
    address public immutable executerAddress;

    // ------------------------ Constructor ------------------------
    constructor(address _executer) {
        executerAddress = _executer;
    }

    // ------------------------ Modifiers ------------------------
    modifier onlyTaskOwner(uint256 taskId) {
        if (tasks[taskId].user != msg.sender) {
            revert Unauthorized(msg.sender, tasks[taskId].user);
        }
        _;
    }

    modifier validTask(uint256 taskId) {
        if (taskId >= taskIdCounter) revert TaskNotFound(taskId);
        _;
    }

    // ------------------------ Externals ------------------------
    /**
     * @notice Schedule a new task via Executer
     * @param executeAt When the task should run (timestamp)
     * @param expiryAt When the task becomes invalid (0 for no expiry)
     * @param target The final target contract
     * @param payload Encoded function call data
     * @param value ETH/value to be sent with the call
     */
    function scheduleTask(
        uint64 executeAt,
        uint64 expiryAt,
        address target,
        bytes calldata payload,
        uint256 value
    ) external returns (uint256 taskId) {
        if (executeAt < block.timestamp + 60) {
            revert InvalidTask("Execution time must be at least 1 minute in future");
        }
        if (expiryAt != 0 && expiryAt <= executeAt) {
            revert InvalidTask("Expiry must be after execution time");
        }
        if (target == address(0)) {
            revert InvalidTask("Target cannot be zero address");
        }

        taskId = taskIdCounter++;
        bytes32 payloadHash = keccak256(payload);

        tasks[taskId] = Task({
            executeAt: executeAt,
            expiryAt: expiryAt,
            user: msg.sender,
            executer: executerAddress,
            target: target,
            payloadHash: payloadHash,
            value: value,
            isCancelled: false
        });

        emit TaskScheduled(
            taskId,
            msg.sender,
            executerAddress,
            target,
            executeAt,
            expiryAt,
            payloadHash,
            value
        );
    }

    /**
     * @notice Cancel a scheduled task
     */
    function cancelTask(uint256 taskId) 
        external 
        validTask(taskId)
        onlyTaskOwner(taskId)
    {
        Task storage task = tasks[taskId];

        if (task.isCancelled) revert TaskAlreadyCancelled(taskId);
        if (block.timestamp >= task.executeAt) {
            revert ExecutionWindowNotReached(task.executeAt, block.timestamp);
        }

        task.isCancelled = true;
        emit TaskCancelled(taskId);
    }

    // ------------------------ Keepers ------------------------
    /**
     * @notice Execute a scheduled task via Executer
     */
    function executeTask(uint256 taskId) 
        external 
        validTask(taskId)
        returns (bool)
    {
        Task memory task = tasks[taskId];

        if (task.isCancelled) revert TaskAlreadyCancelled(taskId);
        if (block.timestamp < task.executeAt) {
            revert ExecutionWindowNotReached(task.executeAt, block.timestamp);
        }
        if (task.expiryAt != 0 && block.timestamp > task.expiryAt) {
            revert ExecutionWindowExpired(task.executeAt, task.expiryAt);
        }

        // prevent re-execution
        tasks[taskId].isCancelled = true;

        // Forward to Executer contract
        bool success = ChainPilotExecutor(task.executer).executeTask{value: task.value}(
            task.user,
            task.target,
            task.payloadHash,
            task.value
        );

        emit TaskExecuted(taskId, msg.sender);
        return success;
    }

    // ------------------------ Views ------------------------
    /**
     * @notice Get pending tasks within a time range
     */
    function getPendingTasks(uint256 from, uint256 to) 
        external 
        view 
        returns (uint256[] memory) 
    {
        if (from > to) revert InvalidTimeWindow(from, to);

        uint256[] memory pendingTasks = new uint256[](taskIdCounter);
        uint256 count = 0;

        for (uint256 i = 0; i < taskIdCounter; i++) {
            Task memory task = tasks[i];
            if (!task.isCancelled && 
                task.executeAt >= from && 
                task.executeAt <= to &&
                (task.expiryAt == 0 || block.timestamp <= task.expiryAt)) {
                pendingTasks[count++] = i;
            }
        }

        assembly { mstore(pendingTasks, count) }
        return pendingTasks;
    }
}

contracts/core/TemplateManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @author @0xJonaseb11
 * @title ChainPilot - Template Manager
 * @notice Stores reusable task templates (e.g., multi-send, DAO voting scripts).
 * @dev Templates are gas-optimized for batch operations and can be cloned by users.
 */
contract TemplateManager {
    // ------------------------ Custom Errors ------------------------
    error Unauthorized();
    error InvalidTemplate();
    error TemplateLocked();
    error ExecutionFailed();

    // ------------------------ Structs & Events ------------------------
    struct Template {
        address owner;
        address target;
        bytes payload;  
        bool isPublic;
        bool isLocked;  
    }
    event TemplateCreated(uint256 indexed templateId, address indexed owner);
    event TemplateUsed(uint256 indexed templateId, address indexed user);

    // ------------------------ Storage ------------------------
    mapping(uint256 => Template) public templates;
    uint256 public templateIdCounter;

    // ------------------------ External Functions ------------------------
    /**
     * @notice Create a new template.
     * @param target Contract to interact with (e.g., Executer.sol).
     * @param payload Encoded batch operation (use ABI.encodePacked for arrays).
     * @param isPublic If true, anyone can use this template.
     */
    function createTemplate(
        address target,
        bytes calldata payload,
        bool isPublic
    ) external returns (uint256 templateId) {
        if (target == address(0) || payload.length == 0) revert InvalidTemplate();

        templateId = templateIdCounter++;
        templates[templateId] = Template(msg.sender, target, payload, isPublic, false);

        emit TemplateCreated(templateId, msg.sender);
    }

    /**
     * @notice Lock a template (makes it immutable).
     * @dev Only the owner can lock.
     */
    function lockTemplate(uint256 templateId) external {
        Template storage template = templates[templateId];
        if (template.owner != msg.sender) revert Unauthorized();
        template.isLocked = true;
    }

    // ------------------------ Template Execution ------------------------
    /**
     * @notice Use a template to execute a batch task.
     * @dev Calls the pre-defined payload via Executer.sol.
     */
    function useTemplate(uint256 templateId) external {
        Template memory template = templates[templateId];
        if (!template.isPublic && template.owner != msg.sender) revert Unauthorized();

        (bool success, ) = template.target.call(template.payload);
        if (!success) revert ExecutionFailed();

        emit TemplateUsed(templateId, msg.sender);
    }
}

